/*Table structure for table `moment` */

DROP TABLE IF EXISTS `moment`;

CREATE TABLE `moment` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `owner_id` bigint unsigned NOT NULL COMMENT '发布者id',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `photo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '朋友圈图片',
  `time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `love` int unsigned NOT NULL DEFAULT '0' COMMENT '点赞数',
  `remark` int unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `view` int unsigned NOT NULL DEFAULT '0' COMMENT '浏览量',
  `status` smallint unsigned NOT NULL DEFAULT '0' COMMENT '状态',
  `gmt_create` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `gmt_modified` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `fk_user_id_4` (`owner_id`) USING BTREE,
  CONSTRAINT `fk_user_id_4` FOREIGN KEY (`owner_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

